<script type="text/javascript">

    function filterFunction() {
        $('ul#warrantyManuLogoList li:gt(14)').hide();
        var input, filter, ul, li, a, i, txtValue;
        input = document.getElementById("filterValue");
        filter = input.value.toUpperCase();
        ul = document.getElementById("warrantyManuLogoList");
        li = ul.getElementsByTagName("li");
        for (i = 0; i < li.length; i++) {
            a = li[i].getElementsByTagName("a")[0];
            var $img = a.getElementsByTagName("img")[0];
            txtValue = $img.alt;
            if (txtValue.toUpperCase().indexOf(filter) > -1) {
                li[i].style.display = "";
            } else {
                li[i].style.display = "none";
            }
        }
    }

    $(document).ready(function () {

        $('ul#warrantyManuLogoList li:gt(14)').hide();

        $(document).on("click", ".viewmoremanulist", function () {
            $('ul#warrantyManuLogoList li:gt(14)').show();
            $(this).removeClass('viewmoremanulist').addClass('hiddenItems');
            $(this).text('Thu gọn');
        });
        $(document).on("click", ".hiddenItems", function () {
            $('ul#warrantyManuLogoList li:gt(14)').hide();
            $(this).removeClass('hiddenItems').addClass('viewmoremanulist');
            $(this).text('Xem thêm các hãng khác');
        });

        $(':radio[name="warrantTypeProduct"]').change(function (e) {
            $.ajax({
                type: 'POST',
                url: '/aj/Warranty/WarrantyManuList',
                data: { categoryID: $(':radio[name="warrantTypeProduct"]:checked').val(), clearcache : true },
                success: function (data) {
                    $("#manuList").html(data);
                    $('ul#warrantyManuLogoList li:gt(14)').hide();
                },
                error: function () {

                }
            });

        });
    })

    $('#filterValue').on('keypress', function (e) {
        if (e.which === 13) {
            filterFunction();
        }
    });
</script>
